entry.value:
import java.time.ZonedDateTime
import groovy.json.JsonSlurper

import com.cronutils.model.definition.CronDefinitionBuilder
import com.cronutils.parser.CronParser
import com.cronutils.model.time.ExecutionTime
import static com.cronutils.model.CronType.QUARTZ 

def flowFile = session.get()
if(!flowFile) return

try {
    // Читаем тело FlowFile, парсим JSON и извлекаем значение поля 'cron'.
    def cronExpression = ''

    session.read(flowFile) { inputStream ->
        try {
            // Используем встроенный в Groovy JsonSlurper для парсинга.
            // .getText('UTF-8') целиком читает поток в строку.
            def json = new JsonSlurper().parseText(inputStream.getText('UTF-8'))
			cronExpression = json.cron?.toString()
        } catch (e) {
            // Оставим cronExpression пустым, если тело не является валидным JSON
            log.warn("Could not parse JSON from FlowFile content for ${flowFile.id}", e)
        }
    }

    // Если CRON-выражение пустое или отсутствовало в JSON, отправляем в failure
    if(!cronExpression || cronExpression.trim().isEmpty()) {
		log.warn("CRON expression is null or empty for FlowFile ${flowFile.id}. Routing to failure.")
        session.transfer(flowFile, REL_FAILURE)
        return
    }

    // Создаем определение CRON-синтаксиса. Мы используем QUARTZ, так как он
    //    поддерживает 7 полей (включая секунды), это совпадает с планировщиком NiFi.
    def cronDefinition = CronDefinitionBuilder.instanceDefinitionFor(QUARTZ)

    // Создаем парсер с этим определением
    def parser = new CronParser(cronDefinition)

    // Парсим CRON-выражение из тела FlowFile
    def quartzCron = parser.parse(cronExpression.trim())

    // Создаем объект ExecutionTime, который умеет делать вычисления по CRON
    def executionTime = ExecutionTime.forCron(quartzCron)

    // Главная проверка: соответствует ли текущее время (с учетом таймзоны) нашему CRON'у?
    //    ZonedDateTime.now() берет системное время сервера NiFi.
    if (executionTime.isMatch(ZonedDateTime.now())) {
        // Время совпало! Отправляем FlowFile на дальнейшую обработку.
        session.transfer(flowFile, REL_SUCCESS)
    } else {
        // Время не совпало. Отправляем FlowFile на удаление.
        session.transfer(flowFile, REL_FAILURE)
    }

} catch(e) {
    // Если произошла любая ошибка (например, невалидный CRON в базе),
    // логируем ее и отправляем FlowFile в failure, чтобы он не блокировал поток.
    log.error("Failed to process cron for FlowFile ${flowFile.id}", e)
    session.transfer(flowFile, REL_FAILURE)
}

